import React, { useEffect } from 'react';
import './App.css'; // Importing the external CSS file
import { useState } from 'react';
import logo from './assets/foodtastic-logo.png';
import pastaPhoto from './assets/pasta_photo.webp';
import saladPhoto from './assets/salad_photo.jpeg';
import friesPhoto from './assets/fries_photo.jpg';

function App() {
  const [recipes, setRecipes] = useState([["Fettuccine alfredo", "butter, heavy cream, parmesan, garlic, pasta"], 
  ["Garlic fries", "potatoes, oil, garlic powder"], 
  ["Salad", "kale, dried fruit, nuts, pita chips"]]);

  // useEffect(() => {
  //   fetch()
  // }, [])

  const [food, setFood] = useState('')
  const [ingredients, setIngredients] = useState('')
  function handleChange(e, target){
    if (target == "food") {
      setFood(e.target.value)
    } else {
      setIngredients(e.target.value)
    }
  };

  const handleAddRecipe = async() => {
    try{
        setTasks([...recipes, [food, ingredients]])
        const res = await axios.post('http://localhost:5001/v1/add-task', {food, ingredient});
        setFood('')
        setIngredient('')
        setResponse(res.data);
    } catch(error){
    }
    };


  return (
    <div className="app">
      <header className="header">
        <div className="logo-container">
          {/* Placeholder for logo */}
          <img src={logo} alt="Logo" className="logo-img" />
          <h1 className="title">Foodtastic</h1>
        </div>
      </header>

      <main className="main-content">
        <h2>Are you starving?</h2>
        <h3>Check out some of our recipes below!</h3>
        <div className="food-images">
          <img src={friesPhoto} alt="Food 3" className="food-img" />
          <img src={pastaPhoto} alt="Food 2" className="food-img" />
          <img src={saladPhoto} alt="Food 3" className="food-img" />
        </div>
      </main>

      <div className="recipes">
        <h1>Recipes</h1>

        <div className="foods">
          {recipes.map((recipe, index) => (
              <div className="food">
                  <p style={{fontWeight: "bold"}}>Food:</p>
                  <p>{recipe[0]}</p>
                  <br />
                  <p style={{fontWeight: "bold"}}>Ingredients:</p>
                  <p>{recipe[1]}</p>
              </div>
          ))}
        </div>

      </div>
    </div>
  );
}

export default App;
